using namespace std;
ostream& operator<<(ostream& os, const pair<int, int>& v)
{
    return os << "(" << v.first << "," << v.second << ")";
}

template <class T>
ostream& operator<<(ostream& os, const vector<T>& v)
{
    os << "[";
    for (const auto& t: v) {
        os << t << " ";
    }
    return os << "]";
}

template <class T, class U>
ostream& operator<<(ostream& os, const map<T, U>& v)
{
    os << "{";
    for (auto& x: v) {
        os << "{" << x.first << ", " << x.second << "} ";
    }
    return os << "}";
}

//naive and TLE
class Solution {
public:
    enum {
        BAD=1, GOOD
    };
    int fixed {0};
    std::unordered_map<int, int> works; // star match at index success or not    

    bool matchStar(const char* s, int n, const char* p, int m) {
        for (int i = 0; i <= n; i++) {
            if (works[n-i] == BAD) continue;
            if (isMatchN(s+i, n-i, p+1, m-1)) {
                return true;
            }
            works[n-i] = BAD;
        }
        return false;
    }

    bool isMatchN(const char *s, int n, const char *p, int m) {
        if (n == 0 || m == 0) {
            if (*p == 0 && *s == 0) return true;
            if (!*s) {
                while (*p == '*') p++;
                if (*p == 0) return true;
            }
            return false;
        }  

        if (*p == '?') {
            s++; p++;
            return isMatchN(s, n-1, p, m-1);

        } else if (*p == '*') {
            return matchStar(s, n, p, m);
        } else {
            if (*p == *s) {
                s++; p++;
                return isMatchN(s, n-1, p, m-1);
            } 
        }
        return false;
    }

    bool isMatch(const char *s, const char *p) {
        int n = 0, m = 0;
        fixed = 0;
        for (int i = 0; *(s+i) && *(p+i); i++) {
            if (*(s+i)) n++;
            if (*(p+i)) m++;
            if (*(p+i) != '*') fixed++;
        }
        while (*(s+n)) n++;
        while (*(p+m)) {
            if (*(p+m) != '*') fixed++;
            m++;
        }
        if (fixed > n) return false;
        return isMatchN(s, strlen(s), p, strlen(p));
    }    
};

//elegant!
class Solution2 {
public:
    bool isMatch(const char *s, const char *p) {        
        const char* lastStar = NULL, * lastS = NULL;
        while (*s) {
            if (*p == '?' || *p == *s) {
                p++; s++; 
            } else if (*p == '*') {
                lastStar = p++; lastS = s;
            } else if (lastStar) {
                p = lastStar+1; s = lastS++;
            } else return false;
        }

        while (*p && *p == '*') p++;
        return *p == 0;
    }
};

void isMatch(const char *s, const char *p)
{
    // cout << s << ", " << p << " -> " << Solution().isMatch(s, p) << endl;
    cout << Solution().isMatch(s, p) << endl;    
    cout << "equal: " << (Solution().isMatch(s, p) == Solution2().isMatch(s, p)) << endl;    
}

int main(int argc, char const *argv[])
{
    isMatch("", "*"); // true
    isMatch("", "**"); // true    
    isMatch("a", "a**"); // true

    isMatch("aa","a"); // → false
    isMatch("aa","aa"); // → true
    isMatch("aaa","aa"); // → false
    isMatch("aa", "*"); // → true
    isMatch("aa", "a*"); // → true
    isMatch("ab", "?*"); // → true
    isMatch("aab", "c*a*b"); // → false
    isMatch("aaabbbaabaaaaababaabaaabbabbbbbbbbaabababbabbbaaaaba", "a*******b"); // false
    isMatch("aaabbbaabaaaaababaabaaabbabbbbbbbbaabababbabbbaaaaba", "a*a*a*b*b***b?"); // true
    isMatch("", "**");
	return 0;
}
